INSERT INTO mengmei_article ( `id`, `catid`, `name`, `seo`, `keywords`, `description`, `editorid`, `editor`, `order`, `ctime`, `utime`, `ptime`, `status`, `hits`, `content`, `image`, `recommend`, `settop`, `weixin`, `pc`, `mobile`, `app`, `wshort`, `isshow`, `isdel`, `copyfrom` ) VALUES ('42','18','2018•鸿•孕 北京 美国试管婴儿公益答疑会！','','','继2018新年鸿孕上海场美国试管咨询会首站告捷，2月3日，梦美HRC美国试管婴儿国内外专家团队遵循“一带一路”众国联手共享前端医疗布局，一路向北来到首都，启动新年第二站美国试管婴儿公益助孕之行。','1','admin','0','2018-08-09','2018-08-09','2018-02-04','1','30','<p class=\"MsoNormal\">
