INSERT INTO mengmei_article ( `id`, `catid`, `name`, `seo`, `keywords`, `description`, `editorid`, `editor`, `order`, `ctime`, `utime`, `ptime`, `status`, `hits`, `content`, `image`, `recommend`, `settop`, `weixin`, `pc`, `mobile`, `app`, `wshort`, `isshow`, `isdel`, `copyfrom` ) VALUES ('74','19','一次面签全通过!16位梦美国内美国试管员工 亲历分享美签经验！NO.2','','','昨日，梦美HRC 16位美国试管工作人员与大家分享了北京美国大使馆的面签详细流程与现场图片
