  `isdel` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否删除：1：是，0：否',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=102 DEFAULT CHARSET=utf8 COMMENT='规则表';
